package Class3Jan7;

public class Prcatice {
	// Write a program to check given year is leap year or not

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int year = 2016;
		if (year % 4 == 0) {
			System.out.print("2016 is a leap year");
		}else {
			System.out.print("2016 is not a leap year");
		}

	}

}
